








CREATE OR REPLACE FUNCTION products_update_stock_randomly() RETURNS VOID AS $$
DECLARE 
    cur CURSOR FOR SELECT id FROM products;
    rec RECORD;
BEGIN
    OPEN cur;

    LOOP
        FETCH NEXT FROM cur INTO rec;
        EXIT WHEN NOT FOUND;

        UPDATE products
        SET stock = floor(random() * (80-10+1) + 10)::integer
        WHERE id = rec.id;
    END LOOP;

    CLOSE cur;
END; $$
LANGUAGE plpgsql;

select products_update_stock_randomly();


CREATE OR REPLACE FUNCTION sequences_update_next_id_tables() RETURNS VOID AS $$
DECLARE
    v_category_next_id INTEGER;
	v_product_next_id INTEGER;
	v_profile_next_id INTEGER;
	v_document_type_next_id INTEGER;
	v_customer_next_id INTEGER;
BEGIN

    v_category_next_id := (SELECT COALESCE(MAX(id) + 1, 1) FROM categories);
	v_product_next_id := (SELECT COALESCE(MAX(id) + 1, 1) FROM products);
	v_profile_next_id := (SELECT COALESCE(MAX(id) + 1, 1) FROM profiles);
	v_document_type_next_id := (SELECT COALESCE(MAX(id) + 1, 1) FROM document_types);
	v_customer_next_id := (SELECT COALESCE(MAX(id) + 1, 1) FROM customers);

    EXECUTE format('ALTER SEQUENCE categories_id_seq RESTART WITH %s', v_category_next_id);
	EXECUTE format('ALTER SEQUENCE products_id_seq RESTART WITH %s', v_product_next_id);
	EXECUTE format('ALTER SEQUENCE profiles_id_seq RESTART WITH %s', v_profile_next_id);
    EXECUTE format('ALTER SEQUENCE document_types_id_seq RESTART WITH %s', v_document_type_next_id);
	EXECUTE format('ALTER SEQUENCE customers_id_seq RESTART WITH %s', v_customer_next_id);

END; $$
LANGUAGE plpgsql;


SELECT sequences_update_next_id_tables();

